            <ul class="error"><?php if(isset($_POST['submit'])) $this->sendmail(); //If the form is submitted ?></ul>                            <?php if(isset($this->email_sent) && $this->email_sent == true): ?>                <p class="thankyou">                    <?php _e('Your Message Has Been Sent, thank you!', THEME_NAME); ?>                </p>            <?php else: ?>                <form id="contactform" action="" method="post">                    <div class="form-input">                        <input id="cfname" name="cfname" class="text <?php if(isset($this->invalid_inputs['name'])) echo 'invalid';?>" type="text" value="<?php echo $this->invalid_inputs['name'];?>" size="30" maxlength="50" tabindex="1" />                        <label class="form-label"><?php _e('Name', THEME_NAME); ?> *</label>                    </div>                    <div class="form-input">                        <input id="cfemail" name="cfemail" class="text <?php if(isset($this->invalid_inputs['email'])) echo 'invalid';?>" type="text" value="<?php echo $this->invalid_inputs['email'];?>" size="30" maxlength="50" tabindex="2" />                        <label class="form-label"><?php _e('EMail', THEME_NAME); ?> *</label>                    </div>                    <div class="form-textarea">                        <textarea id="cfmessage" name="cfmessage" class="textarea <?php if(isset($this->invalid_inputs['message'])) echo 'invalid';?>" tabindex="3" ><?php echo $this->invalid_inputs['message'];?></textarea>                    </div>                                        <p class="form-submit">                        <input id="submit" name="submit" class="button" type="submit" value="<?php _e( 'Send E-Mail', THEME_NAME ) ?>" tabindex="4" />                        <span class="loaderIcon hidden"></span>                        <input type="hidden" value="<?php echo admin_url("admin-ajax.php"); ?>" name="submit_url" id="submit_url">                    </p>                </form>            <?php endif; ?>